// UsersTable.js
import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField } from '@material-ui/core';

const UsersTable = ({ users, onDelete, onLimitChange }) => {
  const [newLimit, setNewLimit] = React.useState('');

  const handleLimitInputChange = (event) => {
    setNewLimit(event.target.value);
  };

  const handleLimitChange = () => {
    if (newLimit && !isNaN(newLimit)) {
      onLimitChange(Number(newLimit));
    }
  };

  return (
    <div>
      <TextField
        label="Set Page Limit"
        type="number"
        value={newLimit}
        onChange={handleLimitInputChange}
      />
      <Button onClick={handleLimitChange}>Set</Button>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Score</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {users.map((user) => (
              <TableRow key={user.id}>
                <TableCell>{user.name}</TableCell>
                <TableCell>{user.email}</TableCell>
                <TableCell>{user.score}</TableCell>
                <TableCell>
                  <Button onClick={() => onDelete(user.id)}>Delete</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default UsersTable;
